select t2.FNames NAME,t1.LOGTIME,t1.LOGTYPE,t1.CONTENT
from SysLog t1 with (nolock)
inner join AC_WorkGroup t2 with (nolock) on t1.Operator=t2.FID
where  1=1 {0}

