select br.PatientCode, --病人编号 br.PatientName, --病人姓名 br.PatientSex, --病人性别 br.PatientBirthday, --出生日期
br.PatientIDCard, --身份证号 br.PatientAddress, --地址 br.PatientPhone, --联系电话 br.HighRiskInfectivity, --高危传染
zy.PatientCode as PatientCodeZY, --病人编号 zy.InPatientCode, --住院号 zy.WardArea, --病区 zy.BedNumber, --床位 zy.BedNurse --床位护士 from surg_make.v_patient_info_gys br left join surg_make.pats_in_hospital_gys zy on br.PatientCode=zy.PatientCode where 1=1 {0}
