select br.PATIENT_ID as PatientID, br.NAME as PatientName, br.SEX as PatientSex, br.DATE_OF_BIRTH as PatientBirthday, br.AGE as PatientAge, br.ID_NO as PatientIDCard, br.MAILING_ADDRESS as PatientAddress,
zy.INP_NO as InPatientID, zy.WARD_CODE as WARDAREA, zy.BED_NO as BedNo, zy.NURSE as BedNurse,
ss.INP_NO as MedicalreCordCode, ss.OPERATING_ROOM as OperationRoom, ss.OPERATING_DEPT as OperationDepartment, ss.REQ_DATE_TIME as OperationDate, ss.SURGEON as OperationDoctor, ss.OPERATION_NAME as OperationName, ss.START_TIME as OperationBegin, ss.END_TIME as OperationEnd from V_INFECON_PATIENTINFO br inner join V_INFECON_PATS_IN_HOSPITAL zy on br.PATIENT_ID=zy.PATIENT_ID left join V_INFECON_OPERATION ss on br.PATIENT_ID=ss.PATIENT_ID where 1=1 {0}
