select br.patient_id PatientID,--病人ID br.name as PatientName, --姓名 br.sex as PatientSex, --性别 br.birth_date as PatientBirthday, --出生日期 br.age as PatientAge, --年龄 br.social_no as PatientIDCard, --身份证号 br.home_street as PatientAddress, --地址 br.home_tel as PatientPhoto, --电话 br.p_bar_code, --就诊号
zy.inpatient_no as InPatientID,--住院号 zy.ward_name as WardName, --病区 zy.bed_no as BedNo, --床位
ss.op_record_ID as MedicalRecordCode,--病案号 ss.group_name as OperationRoom, --手术室 ss.name as OperationDept, --科室 ss.op_datetime as OperationDate, --手术时间 ss.op_doctor as OperationDoctor, --医生 ss.order_name as OperationName, --手术名称 ss.xs_nurse1, --洗手护士1 ss.xs_nurse2, --洗手护士 ss.xh_nurse, --巡回护士1 ss.op_status as OperationStatus, --手术状态 ss.op_starttime as OperationBeginTime, --手术开始时间 ss.op_endtime as OperationEndTime --手术结束时间 from dbo.view_supply_patient_info br left join dbo.view_supply_zy_patient_info zy on br.patient_id=zy.patient_id left join dbo.view_supply_op_info ss on zy.inpatient_no=ss.inpatient_no where 1=1 {0}
