select br.patient_id, --病人编号 br.name, --病人姓名 br.sex, --病人性别 br.date_of_birth, --出生日期 br.age, --年龄 br.id_no, --身份证号 br.sbkh, --社保卡号 br.Next_Of_Kin_Addr, --地址 br.Next_Of_Kin_Phone, --联系电话 br.gw,
zy.patient_id, --病人编号 zy.inp_no, --住院号 zy.bed_no, --床位 zy.duty_nurse, --床位护士
ss.patient_id, --病人编号 ss.operation_no,--病案号 ss.operating_room, --手术室 ss.dept_stayed, --科室 ss.sssj, --手术时间 ss.sequence, --手术台次 ss.operator, --手术医生 ss.operation_desc,--手术名称 ss.first_operation_nurse, --洗手护士1 ss.second_operation_nurse,--洗手护士2 ss.first_supply_nurse, --巡回护士1 ss.second_supply_nurse, --巡回护士2 ss.oper_state, --手术状态 ss.Operating_Date, --手术开始时间 ss.OPERATING_END_DATE --手术结束时间 from pat_basic br left join pat_inp zy on br.patient_id=zy.patient_id left join pat_operation ss on zy.patient_id=ss.patient_id where 1=1 {0}
