select br.PatientCode, --病人编号 br.PatientName, --姓名 br.MedicalRecordCode, --病历号 br.PatientSex, --性别 convert(datetime,br.PatientBirthday,120) as PatientBirthday, --出生年月 year(getdate())-substring(br.PatientBirthday,1,4) as PatientAge, --年龄 br.PatientIDCard, --身份证号 br.SocialSecurityCard, --社保卡号 br.PatientAddress, --地址 br.PatientPhone, --联系电话 br.HighRiskInfectivity, --是否具有高危
zy.InPatientCode, --住院号 zy.WardArea, --病区 zy.BedNumber, --床位 zy.BedNurse, --床位护士
ss.MedicalRecordCode, --病案号 ss.OperationRoom, --手术室 ss.OperationDepartment, --科室 convert(datetime,ss.OperationDate,120) as OperationDate, --手术时间 ss.OperationOrderCode, --手术台次 ss.OperationDoctor, --手术医生 ss.OperationName, --手术名称 ss.OperationStatus, --手术状态 convert(datetime,ss.OperationBegin,120) as OperationBegin, --手术开始时间 convert(datetime,ss.OperationEnd,120) as OperationEnd, --手术结束时间 convert(datetime,ss.InRoomTime,120) as InRoomTime, --入手术室时间 convert(datetime,ss.OutRoomTime,120) as OutRoomTime --出手术室时间 from View_Infecon_Patient br left join View_Infecon_InPatient zy on br.PatientCode=zy.PatientCode left join View_Infecon_Operation ss on zy.PatientCode=ss.PatientCode where 1=1 {0}
